var MainNav={init:function(){$("#nav .nav-dropdown .vr-line, .signup-holder .vr-dotted").each(function(){alert('huhu');$(this).height($(this).parent().height())});$("#nav>ul>li").mouseover(function(){if(!$(this).children().hasClass("current")){$(this).children().addClass("current");$(this).children(".dropdown-container").removeClass("offscreen-render")}});$("#nav>ul>li").mouseout(function(a){if($(this).children().hasClass("current")){$(this).children().removeClass("current");$(this).children(".dropdown-container").addClass("offscreen-render")}});$("#nav .nav-dropdown ul li a").click(function(){$("#nav>ul>li").removeClass("current");$("#nav .dropdown-container").addClass("offscreen-render")});$(".subnav ul.sub-list a.sub").click(function(){if($(this).hasClass("minus")){$(this).removeClass("minus");$(this).addClass("plus");$(this).siblings("ul.sub-sub-list").addClass("hidden");$(this).html("+");return false}if($(this).hasClass("plus")){$(this).removeClass("plus");$(this).addClass("minus");$(this).siblings("ul.sub-sub-list").removeClass("hidden");$(this).html("-");return false}})}};MainNav.init();var Main={init:function(){$(".edit-popup .btn-close").click(function(){$(this).parents(".btn-edit").removeClass("open");$(this).parents(".popup-container").addClass("hidden");if($(this).parent().siblings(".pref_dropdown").children(".dropdown").hasClass("visible")){$(this).children().removeClass("hidden");$(this).removeClass("closed").addClass("open")}this.blur();return false});return false}};